mapper.select:
select p.pno, p.title, tu.user_name, p.img, p.place, p.contents, p.upload_date as upLoadDate, p.price, p.categories, p.uno from practiceroom p left join t_user tu on p.uno=tu.uno
and categories=#{categories}
and tu.uno = #{uno}
limit #{limit} offset #{pageNo}

mapper.insert:
