mapper.select:
select * from sheetmusic
where 1=1

  and genre=#{genre}

limit #{limit} offset #{pageNo}

select s.ino, tu.user_name, s.title, s.viewNumber, s.artist, s.img, s.genre, s.releaseDate, s.albumName, s.musicLink, s.contents, s.uploadDate, s.sheetFile from sheetmusic s left join t_user tu on s.uno = tu.uno where 1=1
and s.uno=#{uno}
and s.genre=#{genre}
limit #{limit} offset #{pageNo}
